create table events
(
    x           integer not null,
    y           integer not null,
    constraint events_pk
        primary key (y, x),
    type        varchar not null,
    title       varchar not null,
    description varchar not null,
    action      varchar,
    removable   boolean not null
);

create table events_in_game
(
    x           integer not null,
    y           integer not null,
    constraint events_in_game_pk
        primary key (y, x),
    game_id uuid not null
        constraint event_in_game_game_id_fk
            references game,
    type        varchar not null,
    title       varchar not null,
    description varchar not null,
    action      varchar,
    removable   boolean not null
);

CREATE EXTENSION IF NOT EXISTS "uuid-ossp";

