INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 3, 'Znalazłeś bardzo ciekawy okaz muszelki, która należała do niezwykle rzadko spotykanego ślimaka morskiego Strombus Gigas.
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 6, 'Na zachrypniętą papugę!
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 9, 'Fortuna wam sprzyja – ciepły prąd zatokowy El Ninio, pojawiający się na tutejszych wodach raz na cztery lata właśnie się pojawił! Dzięki niemu zyskujecie trochę czasu, gdyż z siłą wodospadu ponosi was akurat w tym kierunku, w którym chcecie płynąć! Zatem możecie teraz wykonać dodatkowy ruch.', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 11, 'Rum, który otrzymałeś w zaprzyjaźnionej acz nielegalnej gorzelni nieźle daje niezłego kopa! Postanawiasz ukryć go przed załogą i zachować na później. Ty zadecydujesz kiedy opróżnicie baryłki niezwykłego trunku. Dzięki temu załoga dostanie nowej energii i będzie się uwijać dwa razy szybciej niż normalnie a przez to w jednej turze pokonacie odległość dwóch pól – zgłoś ten fakt przed wykonaniem ruchu, w dowolnym momencie rejsu (nie dotyczy was ewentualna zawartość pola przez które tylko przepływacie, oczywiście poza głębokością toru wodnego)', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 13, 'A niech to mewa kopnie! Tylko tego brakowało, żeby rozpętała się tropikalna burza skoro do portu jest już tak blisko! Mimo zażartej walki z żywiołem o zachowanie życia i towaru, tracisz część cennego ładunku – możesz wybrać czego tracisz 10 ton: kawy, trzciny cukrowej czy cytrusów. Ładunek wypada za burtę i tyle go widzieli.', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 14, 'Wyłowiłeś skrzynię pełną skarbów! Oprócz prawie nieużywanych kalesonów, kapeluszy i opasek na oko pirata, znalazłeś w środku 10 ton wanilii.', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 16, 'Rum, który otrzymałeś w zaprzyjaźnionej acz nielegalnej gorzelni nieźle daje niezłego kopa! Postanawiasz ukryć go przed załogą i zachować na później. Ty zadecydujesz kiedy opróżnicie baryłki niezwykłego trunku. Dzięki temu załoga dostanie nowej energii i będzie się uwijać dwa razy szybciej niż normalnie a przez to w jednej turze pokonacie odległość dwóch pól – zgłoś ten fakt przed wykonaniem ruchu, w dowolnym momencie rejsu. (nie dotyczy was ewentualna zawartość pola przez które tylko przepływacie, oczywiście poza głębokością toru wodnego)', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 19, 'Znalazłeś bardzo ciekawy okaz muszelki, która należała do niezwykle rzadko spotykanego ślimaka morskiego Strombus Gigas. W porcie będziesz mógł za nią dostać 5 sztabek złota. Jeśli dodatkowo już teraz poprawnie odpowiesz na zagadkę, będziesz mógł wliczyć do swojego bilansu na koniec gry 10 ton herbaty i 20 ton kawy (wliczają się do realizacji zamówienia handlowego, nie wliczają do ładowności statku).', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 23, 'Na zachrypniętą papugę!
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 25, 'Oto na horyzoncie zamajaczyły jakieś przedmioty w wodzie. Po dopłynięciu okazało się, że jakiś inny statek miał pecha bo zmiotło mu z pokładu w czasie ostatniej burzy trochę ładunku. Po wyłowieniu okazało się, że uzbieraliście w ten sposób 60 ton kawy i 20 ton trzciny cukrowej. Udane połowy! ', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 27, 'Fortuna wam sprzyja – ciepły prąd zatokowy El Ninio, pojawiający się na tutejszych wodach raz na cztery lata właśnie się pojawił! Dzięki niemu zyskujecie trochę czasu, gdyż z siłą wodospadu ponosi was akurat w tym kierunku, w którym chcecie płynąć! Zatem możecie teraz wykonać dodatkowy ruch.', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 32, 'Wyłowiłeś skrzynię do połowy pełną skarbów! Oprócz zapasu drewnianych nóg, znalazłeś w środku 30 ton tytoniu.', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 34, 'Oto na horyzoncie zamajaczyły jakieś przedmioty w wodzie. Po dopłynięciu okazało się, że jakiś inny statek miał pecha bo zmiotło mu z pokładu w czasie ostatniej burzy trochę ładunku. Po wyłowieniu okazało się, że uzbieraliście w ten sposób 30 ton tytoniu, 10 ton wanilii, 30 ton kakao i 10 ton trzciny cukrowej. Udane połowy!', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 35, 'Cała załoga zatruła się nieświeżymi rybami – wymiotom i biegunkom nie ma końca! W związku z tym nieszczęśliwym incydentem tracicie na kotwicy jeden dzień (jedną turą). (jeśli znasz recepturę lekarstwa na zatrucie pokarmowe możesz wyleczyć załogę i uniknąć straty czasu)', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'adventure', 36, 'Cała załoga zatruła się nieświeżymi rybami – wymiotom i biegunkom nie ma końca! W związku z tym nieszczęśliwym incydentem tracicie na kotwicy jeden dzień (jedną turą). (jeśli znasz recepturę lekarstwa na zatrucie pokarmowe możesz wyleczyć załogę i uniknąć straty czasu)', false);
