INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 1, 'W butelce znajduje się weksel In blanco. Jeśli staniesz na jakimś polu z innym statkiem, możesz przedstawić weksel do zapłaty – zabierasz 10 ton towaru który chcesz ale w zamian zostawiasz 10 ton czegoś, co ci tylko zajmuje miejsce w ładowni. Jeśli staniesz na jednym polu z innym statkiem ale nie skorzystasz z weksla, traci on swoją ważność i staje się bezużytecznym kawałkiem papieru.', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 2, 'Znalazłeś butelkę a w środku było 10 ton bawełny', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 4, 'Kapitan Kuternoga był ci winien trochę herbaty. Niniejszy list jest kwitem depozytowym na 40 ton herbaty do realizacji w porcie. Herbata ta nie wlicza się do wagi statku ani do realizacji zamówienia handlowego. Wlicza się do ogólnego wyniku finansowego na koniec gry po cenach oficjalnych, możesz tym kwitem handlować jak każdym innym dobrem.', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 5, 'GÓRSKIE POWIETRZE Z ANDÓW', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 7, 'Znalazłeś butelkę a w środku było 30 ton kakao.', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 8, 'W butelce znajduje się stary pergamin a na nim zaklęcie jasnowidzenia. WOW! Ekstra sprawa takie jasnowidzenie! Nie wiesz co to jest ale już to chcesz! Na wszelki wypadek każesz ewakuować załogę do szalup kiedy ty wypowiesz zaklęcie na głos. Huknęło, gruchnęło, pokład spowił dym jak po wybuchu beczki prochu ale ty widzisz wszystko wyraźnie jak nigdy! Sięgasz wzrokiem dużo dalej niż pozwala horyzont... (Sprawdź co kryje jedno, wybrane sąsiednie do waszej aktualnej pozycji pole. Załoga wraca na pokład a ty z miną admirała floty zarządzasz kierunek rejsu i ruszacie w dalszą podróż)', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 10, 'W butelce znajduje się weksel In blanco. Jeśli staniesz na jakimś polu z innym statkiem, możesz przedstawić weksel do zapłaty – zabierasz 10 ton towaru który chcesz ale w zamian zostawiasz 10 ton czegoś, co ci tylko zajmuje miejsce w ładowni. Jeśli staniesz na jednym polu z innym statkiem ale nie skorzystasz z weksla, traci on swoją ważność i staje się bezużytecznym kawałkiem papieru.', false);
