INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 18, 'Znalazłeś butelkę w której siedział Dżin Dariusz. Jeśli odgadniesz jego zagadkę spełni jedno twoje życzenie – albo otrzymasz 30 ton kakao albo 30 ton kawy. Jeśli nie odpowiesz lub odpowiesz nieprawidłowo Dżin zabiera ci 10 ton tytoniu i znika z towarem.', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 20, 'W butelce znajduje się kwit depozytowy o wartości 10 sztuk złota na okaziciela, do zrealizowania w porcie do którego płyniecie! A to ci fart!', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 21, 'Znalazłeś butelkę a w środku było 30 ton kakao.', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 22, 'W butelce znajduje się potargany list o złowrogim tytule „Lista zakupów”. Niestety poza listą zakupów składników niezbędnych do wydania niedzielnego obiadu u Gubernatora Wyspy nie ma nic więcej! Zachowam go na później, może się do czegoś przyda...', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 24, 'W butelce znajduje się stary pergamin a na nim zaklęcie jasnowidzenia. WOW! Ekstra sprawa takie jasnowidzenie! Nie wiesz co to jest ale już to chcesz! Na wszelki wypadek każesz ewakuować załogę do szalup kiedy ty wypowiesz zaklęcie na głos. Huknęło, gruchnęło, pokład spowił dym jak po wybuchu beczki prochu ale ty widzisz wszystko wyraźnie jak nigdy! Sięgasz wzrokiem dużo dalej niż pozwala horyzont... (Sprawdź co kryje jedno, wybrane sąsiednie do waszej aktualnej pozycji pole. Załoga wraca na pokład a ty z miną admirała floty zarządzasz kierunek rejsu i ruszacie w dalszą podróż)', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 26, 'W butelce znajduje się niedopita resztka jakiejś berbeluchy. Sądząc po smrodzie cieczy raczej nie kosztowała majątku. Na wszelki wypadek żeby nikt się nie zatruł wyrzucasz butelkę za burtę, zły że w ogóle zawracałeś sobie nią głowę! Czego spodziewałeś się znaleźć w pustej flaszce? Mapy ze skarbami piratów???', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 28, 'Kapitan Drewniana Papuga był ci winien trochę bawełny. Niniejszy list jest kwitem depozytowym na 50 ton bawełny do realizacji w porcie. Bawełna ta nie wlicza się do wagi statku ale wlicza się do realizacji zamówienia handlowego w porcie i ogólnego wyniku finansowego na koniec gry po cenach oficjalnych, możesz tym kwitem handlować jak każdym innym dobrem.', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 29, 'Znalazłeś butelkę w której siedział Dżin Wacław. Jeśli odgadniesz jego zagadkę spełni jedno twoje życzenie – albo otrzymasz 30 ton trzciny cukrowej albo 30 ton bawełny. Jeśli nie odpowiesz lub odpowiesz nieprawidłowo Dżin zabiera ci 10 ton kakao i znika z towarem.', false);
INSERT INTO events (id, type, number, description, removable) VALUES (uuid_generate_v4(), 'bottle', 30, 'W butelce znajduje się potargany list o złowrogim tytule „Lista zakupów”. Niestety poza listą zakupów składników niezbędnych do wydania niedzielnego obiadu u Gubernatora Wyspy nie ma nic więcej! Zachowam go na później, może się do czegoś przyda…', false);
